import React, { Component } from 'react';
import Modal from './Modal';
import data from './Data';
import './css/App.css';


class Header extends Component {
  render () {
    return(
      <div className="header">
        Chester, Cheshire
        <div className="subtitle"> 
          Through my eyes
        </div>
      </div>
    )
  }
}

class Menu extends Component {
  render() {

    let menuItems = [];
    let menuItemData = data;

    for (var i = 0; i < menuItemData.length; i++) {
      menuItems.push(
        <div class="menu-item" key={'menu-'+i}>
          {this.menuItemData[i].name}
        </div>);
    }

    return (
      <div className="menu">
        This is the sticky float meny
      </div>);
  }
}

class ModalContent extends Component {
  render() {
    return (
      <div>

        {this.props.content}
        {this.props.name}
        {this.props.imgUrl}
      </div>
    );
  }
}

class GridItem extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      name: null, 
      imgUrl: null,
      content: null,
      isOpen: false
    }
  }

  toggleModal = () => {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }

  render () {
    return(
      // this is the squares in the grid that you see which have the names of the places
      <div className="grid-item" onClick={this.toggleModal}>
        <img className="background" src={this.props.imgUrl} alt={this.props.name} />
        <div className="location">
          {this.props.name}
        </div>
        <Modal show={this.state.isOpen}
          onClose={this.toggleModal}>
          <ModalContent 
            name={this.props.name} 
            imgUrl={this.props.imgUrl} 
            content={this.props.content}>
          </ModalContent>
        </Modal>
      </div>
    )
  }
}

class PlaceGrid extends Component {
  render () {
    let gridItems = [];
    let gridItemData = data;

    for (var i = 0; i < gridItemData.length; i++) {
      gridItems.push(
        <GridItem 
          name={gridItemData[i].name} 
          imgUrl={gridItemData[i].imgUrl}
          content={gridItemData[i].content} 
          key={i}
        />);
    }


    return(
      <div className="grid">
        {gridItems}
      </div>
    )
  }
}

class App extends Component {

  render() {
    return(
      <div>
        <Menu />
        <Header /> 
        <PlaceGrid /> 
      </div>
    )
  }
}

export default App;
