import React, { Component } from 'react';
import './App.css';


class Header extends Component {
  render () {
    return(
      <div className="header">
        Chester, Cheshire
        <div className="subtitle"> 
          Through my eyes
        </div>
      </div>
    )
  }
}

class GridItem extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      name: null, 
      imgUrl: null,
      content: null
    }
  }

  render () {
    const images = require.context("../public/assets", true);
    let imgsrc = images('./${imgUrl}');

    return(
      // this is the squares in the grid that you see which have the names of the places
      <div className="grid-item" onClick={() => alert("clicked")}>
        <div className="background">
          <img src={require('../public/assets/chester-cathedral.png')} alt={this.props.name} />
          <div className="name">
            {this.props.name}
          </div>
          <div className="content">
            {this.props.content}
          </div>
        </div>
      </div>
    )
  }
}

class PlaceGrid extends Component {
  getData() {
    var data = [
      {
        "name": "Guilden Sutton",
        "imgUrl": "./guilden-sutton.png",
        "content": "test data"
      },
      {
        "name": "Chester Cathedral",
        "imgUrl": "./chester-cathedral.png",
        "content": "test data"
      }
    ];

    return data;
  }

  render () {
    let gridItems = [];
    let gridItemData = this.getData();

    for (var i = 0; i < gridItemData.length; i++) {
      gridItems.push(
        <GridItem 
          name={gridItemData[i].name} 
          imgUrl={gridItemData[i].imgUrl}
          content={gridItemData[i].content} 
        />);
    }


    return(
      <div className="grid">
        {gridItems}
      </div>
    )
  }
}

class App extends Component {

  render() {
    return(
      <div>
        <Header /> 
        <PlaceGrid /> 
      </div>
    )
  }
}

export default App;
