import React, { Component } from 'react';
import Modal from './Modal';
import data from './Data';
import Merton from './assets/merton.jpg';
import './css/App.css';


class Header extends Component {
  render () {
    return(
      <div className="header-area">
        <div className="main-header">
          Chester, Cheshire
          <div className="subtitle"> 
            Through my eyes
          </div>
        </div>
        <div className="description">
          Chester is a beautiful city on the River Dee in England, U.K. 
          The city was originally the Roman fort 
          &nbsp;<a href="https://en.wikipedia.org/wiki/Deva_Victrix" rel="noopener noreferrer" target="_blank">
            <i>Deva Victrix</i>
          </a>&nbsp;
          and later became a settlement. It has a well-preserved wall surrounding the city center, 
          a Roman amphitheater as well as its own castle close to the border of Wales. 
          <br/>
          <br/>
          <div class="sub-section">
          <h1>What this city means to me</h1>
          I lived here for 3 years (2001-2004). It was filled with lots of travel, life-changing experiences and fond memories.
          <div className="description-image">
            <img src={Merton} alt="Merton House Class"/>
          </div>
          </div>
        </div>
      </div>
    )
  }
}

class ModalContent extends Component {
  render() {
    return (
      <div class="modal-content">
        <div class="header">
          <span class="header-name">{this.props.name}</span> 
        </div>
        {this.props.content}
      </div>
    );
  }
}

class GridItem extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      name: null, 
      imgUrl: null,
      content: null,
      isOpen: false
    }
  }

  toggleModal = () => {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }

  render () {
    return(
      // this is the squares in the grid that you see which have the names of the places
      <div className="grid-item" onClick={this.toggleModal}>
        <img className="background" src={this.props.imgUrl} alt={this.props.name} />
        <div className="location">
          {this.props.name}
        </div>
        <Modal show={this.state.isOpen}
          onClose={this.toggleModal}>
          <ModalContent 
            name={this.props.name} 
            imgUrl={this.props.imgUrl} 
            content={this.props.content}>
          </ModalContent>
        </Modal>
      </div>
    )
  }
}

class PlaceGrid extends Component {
  render () {
    let gridItems = [];
    let gridItemData = data;

    for (var i = 0; i < gridItemData.length; i++) {
      gridItems.push(
        <GridItem 
          name={gridItemData[i].name} 
          imgUrl={gridItemData[i].imgUrl}
          content={gridItemData[i].content} 
          key={i}
        />);
    }


    return(
      <div className="grid">
        {gridItems}
      </div>
    )
  }
}

class App extends Component {

  render() {
    return(
      <div>
        <Header /> 
        <PlaceGrid /> 
      </div>
    )
  }
}

export default App;
